import { StyleSheet } from 'react-native';
const styles = StyleSheet.create({

    // Intro Screen Styling
    
    IntroContainer: {
        flex: 1,
        backgroundColor: '#000000',
        alignItems: 'center',
        justifyContent: 'center',
    },

    introTitle: {
        fontSize: 65,
        fontFamily: 'Segoe-ui-bold',
        color: 'white',
    }

});

export { styles }
